mapper.select:
    select
    
      distinct
    
    
    from t_borrowing_loan temp_borrowing_loan
	
    
      
    
    
      order by ${orderByClause}
    
    
      limit ${limitStart} , ${limitEnd}

    select 
    
    from t_borrowing_loan temp_borrowing_loan
	
    where temp_borrowing_loan.loan_id = #{loanId,jdbcType=VARCHAR}
