mapper.select:
select

  distinct


from t_tender_repayment temp_tender_repayment

  


  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}

select 

from t_tender_repayment temp_tender_repayment
where temp_tender_repayment.tender_repayment_id = #{tenderRepaymentId,jdbcType=VARCHAR}

mapper.insert:
insert into t_tender_repayment (tender_repayment_id, repayment_type_id, 
  loan_tender_id, remark, creater, 
  create_time, updater, update_time
  )
values (#{tenderRepaymentId,jdbcType=VARCHAR}, #{repaymentTypeId,jdbcType=VARCHAR}, 
  #{loanTenderId,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, #{creater,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updater,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )
